import React from "react"
import NavBarCss from '../css/NavBar.css'
class NavBar extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      sitename: 'matthews-tyler.com',
      titleLink: 'http://google.com',
      dropdown: 'dropdown-hide',
      dropdownButton: 'dropdownButton-show'
    }
  }

  openDropdown = () => {
    this.setState({
      dropdownButton: 'dropdownButton-hide',
      dropdown: 'dropdown-show'
    });
    document.addEventListener('click', this.closeDropdown);
  }

  closeDropdown = () => {
    this.setState({
      dropdownButton: 'dropdownButton-show',
      dropdown: 'dropdown-hide'
    });
    document.removeEventListener('click', this.closeDropdown);
  }


  render(){
    return(
      <div class="navbar">
      {/*Stuff here*/}
        <div class="row test">

        {/*DEFAULT -- TITLE ALWAYS THERE*/}

          {/*MOBILE BELOW*/}
            <div class="col-1-mobile">
              <a href={this.state.titleLink} id="title" class="text"><p>Matthews-Tyler.com</p></a>
            </div>
            <div class="col-1-mobile"> {/*Dead Space*/}
            </div>
            <div class="col-1-mobile"> {/*Dead Space*/}
            </div>
            <div class="col-1-mobile">
              <p onClick={this.openDropdown} class={this.state.dropdownButton + ' text'}>Test</p>
              <div class={this.state.dropdown}>
                <p class="text">About</p>
                <p class="text">Links</p>
                <p class="text">Projects</p>
                <p class="text">Contact</p>
              </div>
            </div>

          {/*MIDSISZED BELOW*/}
            <div class="col-1-midSize">
              <a href={this.state.titleLink} id="title" class="text"><p>Matthews-Tyler.com</p></a>
            </div>
            <div class="col-6">
            </div>

            <div class="col-1">
              <p class="text">About</p>
            </div>

            <div class="col-1">
              <p class="text">Projects</p>
            </div>

            <div class="col-1">
              <p class="text">Links</p>
            </div>

            <div class="col-1">
              <p class="text">Contact</p>
            </div>
          {/*DESKTOP BELOW*/}
            <div class="col-2">
              <a href={this.state.titleLink} id="title" class="text"><p>Matthews-Tyler.com</p></a>
            </div>
            <div class="col-6">
            </div>

            <div class="col-1">
              <p class="text">About</p>
            </div>

            <div class="col-1">
              <p class="text">Projects</p>
            </div>

            <div class="col-1">
              <p class="text">Links</p>
            </div>

            <div class="col-1">
              <p class="text">Contact</p>
            </div>
        </div>
      </div>
    )
  }
}

export default NavBar;
